package Demo;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int v=3;
		int k=5;
		int g=v+k;
		int f=k-v;
		int t=v*k;
		int y=k%v;
		int p=k/v;
		System.out.println(""+g);
		System.out.println(""+f);
		System.out.println(""+t);
		System.out.println(""+y);
		System.out.println(""+p);
	}

}
